var todo_tab_idx;
$('#pctodotabmodal').on('show.bs.modal', function (event) {
	todo_tab_idx = $(event.relatedTarget).data('idx');
})
function addTodoTabTap(){
	var obj = {
		check:false,
		content:$('#pctodocontent').val(),
	};
	todoAry[todo_tab_idx].ary.push(obj);
	todoForeach();
	$('#pctodotabmodal').modal('hide');
};